library(tidyverse)

mtcars |&gt;
  group_by(cyl) |&gt;
    summarise(disp = mean(disp),
              mpg = mean(mpg)) |&gt;
    ungroup() |&gt;
  pivot_longer(cols = c(disp, mpg),
